CREATE OR REPLACE VIEW alkis.strassen AS 
 SELECT p.ogc_fid,
    l.gml_id,
    COALESCE(p.schriftinhalt, k.bezeichnung) AS schriftinhalt,
    p.art,
    p.horizontaleausrichtung AS hor,
    p.vertikaleausrichtung AS ver,
    (p.drehwinkel * (57.296)::double precision) AS winkel,
    p.wkb_geometry
   FROM ((alkis.ap_pto p
     JOIN alkis.ax_lagebezeichnungohnehausnummer l ON ((l.gml_id = ANY (p.dientzurdarstellungvon))))
     JOIN alkis.ax_lagebezeichnungkatalogeintrag k ON (((((l.kreis)::text = (k.kreis)::text) AND ((l.gemeinde)::text = (k.gemeinde)::text)) AND ((l.lage)::text = (k.lage)::text))))
  WHERE (((p.endet IS NULL) AND (l.endet IS NULL)) AND (k.endet IS NULL));
