--
-- Name: untergang(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION untergang() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			aenderungszeit TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			loeschsperre BOOLEAN;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
			IF loeschsperre AND OLD.gueltig_bis > aenderungszeit THEN
				EXECUTE '
					UPDATE
						' || TG_TABLE_SCHEMA || '.' || TG_TABLE_NAME || '
					SET
						gueltig_bis = $2
					WHERE
						id = $1
				'
				USING OLD.id, aenderungszeit;
				RAISE NOTICE '%.% id: % nicht mehr gültig seit: %', TG_TABLE_SCHEMA, TG_TABLE_NAME, OLD.id, OLD.gueltig_bis;
			END IF;
			RETURN OLD;
		END;
	$_$;


--
-- Name: FUNCTION untergang(); Type: COMMENT; Schema: ukos_okstra; Owner: -
--

COMMENT ON FUNCTION untergang() IS 'Setzt die Gültigkeit des Datensatzes OLD.id in der Tabelle in der das Event auftritt auf das aktuelle Datum und läßt ihn damit untergehen, wenn der Parameter Löschsperre auf true gesetzt ist. Der Rückgabewert ist OLD, damit danach auch noch Trigger ausgeführt werden können.';


