CREATE FUNCTION update_topogeom() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		BEGIN
			--------------------------------------------------------------------------------------------------------
			EXECUTE '
				SELECT
					topology.ST_ChangeEdgeGeom(
						' || quote_literal('ukos_topo') || ',
						(
							SELECT
								e[1]
							FROM
								(
									SELECT
										topology.GetTopoGeomElements(liniengeometrie_topo) e
									FROM
