-- Name: delete_topo_func(character varying); Type: FUNCTION; Schema: ukos_base; Owner: -
--

CREATE FUNCTION ukos_base.delete_topo_func(strassenelement_id character varying) RETURNS boolean
    LANGUAGE plpgsql STRICT
    AS $_$
  DECLARE
    edge_id      INTEGER;
    loeschsperre BOOLEAN;
    debug        BOOLEAN = false;
  BEGIN
    -- Initialisierung
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
    EXECUTE 'SELECT value::BOOLEAN FROM ukos_base.config WHERE key = $1' USING 'Löschsperre' INTO loeschsperre;
    IF debug THEN RAISE NOTICE 'Führe Funktion % aus.', 'ukos_base.delete_topo_func'; END IF;

    IF debug THEN RAISE NOTICE 'Frage TopoGeomElement des Objektes: % ab.', strassenelement_id; END IF;
    EXECUTE FORMAT('
      SELECT
        e[1]
      FROM
        (
          SELECT
            topology.GetTopoGeomElements(liniengeometrie_topo) e
          FROM
            ukos_okstra.strassenelement
          WHERE
            id = %1$L
          LIMIT 1
        ) e_tab
    ', strassenelement_id.id)
    INTO edge_id;

    IF loeschsperre THEN
      IF debug THEN RAISE NOTICE 'Setze TopoGeom des Strassenelementes ID: % zurück.', strassenelement_id; END IF;
      EXECUTE FORMAT('
        UPDATE
          ukos_okstra.strassenelement
        SET
          liniengeometrie_topo = topology.clearTopoGeom(liniengeometrie_topo)
        WHERE
          id = %1$L
      ', strassenelement_id);
    ELSE
      IF debug THEN RAISE NOTICE 'Lösche TopoGeom des Strassenelementes ID: % in Relations.', strassenelement_id; END IF;
      EXECUTE FORMAT('
        SELECT
          topology.clearTopoGeom(liniengeometrie_topo)
        FROM
          ukos_okstra.strassenelement
        WHERE
          id = %1$L
      ', strassenelement_id);
    END IF;

    IF edge_id IS NOT NULL THEN
      IF debug THEN RAISE NOTICE 'Entferne Edge: %', edge_id; END IF;
      EXECUTE FORMAT('SELECT topology.ST_RemEdgeModFace(' || quote_literal('ukos_topo') || ', %1$s)', edge_id);
    END IF;

    IF debug THEN RAISE NOTICE 'Entferne isolierte Punkte.'; END IF;
    EXECUTE '
      SELECT
        topology.ST_RemoveIsoNode(' || quote_literal('ukos_topo') || ', node_id)
      FROM
        ukos_topo.node
      WHERE
        containing_face = 0
    ';

    IF debug THEN RAISE NOTICE 'Beende Funktion %.', 'ukos_base.delete_topo_func'; END IF;
    RETURN true;
  END;
$_$;


