--
-- Name: snap_to_net(); Type: FUNCTION; Schema: ukos_base; Owner: -
--

CREATE FUNCTION ukos_base.snap_to_net() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
  DECLARE
    snap_point   geometry;
    tolerance   NUMERIC;
    accuracy     NUMERIC;
    debug       BOOLEAN = false;
  BEGIN
    -- Initialisierung
    EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;
    EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Koordinatengenauigkeit' INTO accuracy;
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
    IF debug THEN RAISE NOTICE 'Führe Funktion % in Trigger % on % aus.', 'snap_to_net', TG_NAME, TG_TABLE_NAME; END IF;

    IF TG_TABLE_NAME = 'verbindungspunkt' THEN
      NEW.punktgeometrie = ST_SnapToGrid(ukos_base.snap_to_topo(NEW.punktgeometrie, tolerance, format('VP: %s', NEW.id)), accuracy);
      IF debug THEN RAISe NOTICE 'Snap Punkt to Grid an Position: %', ST_AsText(NEW.punktgeometrie); END IF;
    END IF;

    IF TG_TABLE_NAME = 'strassenelement' THEN
      IF TG_OP = 'INSERT' OR NOT ST_Equals(ST_StartPoint(OLD.liniengeometrie), ST_StartPoint(NEW.liniengeometrie)) THEN
        snap_point = ukos_base.find_snap_point(ST_StartPoint(NEW.liniengeometrie), tolerance, format('Anfangspunkt von SE: %s', NEW.id));
        IF NOT ST_Equals(ST_StartPoint(NEW.liniengeometrie), snap_point) THEN
          NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, 0, snap_point);
        END IF;
      END IF;
      IF TG_OP = 'INSERT' OR NOT ST_Equals(ST_EndPoint(OLD.liniengeometrie), ST_EndPoint(NEW.liniengeometrie)) THEN
        snap_point = ukos_base.find_snap_point(ST_EndPoint(NEW.liniengeometrie), tolerance, format('Endpunkt von SE: %s', NEW.id));
        IF NOT ST_Equals(ST_EndPoint(NEW.liniengeometrie), snap_point) THEN
          NEW.liniengeometrie = ST_SetPoint(NEW.liniengeometrie, -1, snap_point);
        END IF;
      END IF;
    END IF;

    IF debug THEN RAISE NOTICE 'Beende Funktion % in Trigger % on % mit RETURN NEW.', 'snap_to_net', TG_NAME, TG_TABLE_NAME; END IF;
    RETURN NEW;
  END;
$_$;


--
-- Name: FUNCTION snap_to_net(); Type: COMMENT; Schema: ukos_base; Owner: -
--

COMMENT ON FUNCTION ukos_base.snap_to_net() IS 'Snappt Verbindungspunkte oder Start- und Endpunkte von Strassenelementen auf vorhandene Verbindungspunkte im Toleranzradius. Snappt zusätzlich Start- und Endpunkte von Strassenelementen auf vorhandene Strassenelemente im Toleranzabstand, falls diese nicht vorher schon auf VP gesnappt sind.';


--
-- Name: snap_to_topo(public.geometry, double precision, character varying); Type: FUNCTION; Schema: ukos_base; Owner: -
--

CREATE FUNCTION ukos_base.snap_to_topo(point public.geometry, tolerance double precision, label character varying) RETURNS public.geometry
    LANGUAGE plpgsql STRICT
    AS $_$
    DECLARE
      snap_point       geometry;
      current_query   text = current_query();
      debug           BOOLEAN = false;
    BEGIN

      -- Initialisierung
      EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
      IF debug THEN RAISE NOTICE 'Führe Funktion % aus.', 'ukos_base.snap_to_topo'; END IF;

      EXECUTE '
        SELECT geom
        FROM ukos_topo.node
        WHERE ST_DWithin(geom, $1, $2)
      '
      USING point, tolerance
      INTO snap_point;
      IF snap_point IS NULL THEN
        IF debug THEN RAISE NOTICE 'Kein anderer Node in der Toleranz des neuen Punktes.'; END IF;
        EXECUTE '
          SELECT ST_LineInterpolatePoint(geom, ST_LineLocatePoint(geom, $1))
          FROM ukos_topo.edge_data
          WHERE ST_DWithin(geom, $1, $2)
          LIMIT 1
        '
        USING point, tolerance
        INTO snap_point;
        IF snap_point IS NULL THEN
          IF debug THEN RAISE NOTICE 'Kein SE in der Toleranz des neuen Punktes.'; END IF;
          snap_point = point;
        ELSE
          IF debug THEN RAISE NOTICE 'Snap Punkt auf Strassenelement an Lotfußpunkt: %', ST_AsText(snap_point); END IF;
          PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf Fusspunkt %s', label, ST_AsText(snap_point)));
        END IF;
      ELSE
        IF debug THEN RAISE NOTICE 'Snap Punkt auf Node an Position: %', ST_AsText(snap_point); END IF;
        PERFORM ukos_base.log(current_query, ''::name, '', '', format('%s.%s', '', ''), format('Snap %s auf Node %s', label, ST_AsText(snap_point)));
      END IF;

      IF debug THEN RAISE NOTICE 'Beende Funktion %.', 'ukos_base.snap_to_topo'; END IF;
      RETURN snap_point;
    END;
    $_$;


--
-- Name: FUNCTION snap_to_topo(point public.geometry, tolerance double precision, label character varying); Type: COMMENT; Schema: ukos_base; Owner: -
--

COMMENT ON FUNCTION ukos_base.snap_to_topo(point public.geometry, tolerance double precision, label character varying) IS 'Return position of node found in tolerance of point or if not found the plumb foot point on an edge in the distance of tolerance to point or if also not found the of the point itself';


--
-- Name: snap_to_topology(); Type: FUNCTION; Schema: ukos_base; Owner: -
--

CREATE FUNCTION ukos_base.snap_to_topology() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
    DECLARE
      fusspunkt RECORD;
      vp RECORD;
      tolerance NUMERIC;
      aenderungszeit  TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
    BEGIN
      -- Initialisierung
      EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

      IF TG_TABLE_NAME = 'verbindungspunkt' THEN
        NEW.punktgeometrie = ukos_base.snap_to_topo(NEW.punktgeometrie, tolerance);
      END IF;
      
      IF TG_TABLE_NAME = 'strassenelement' THEN
        NEW.liniengeometrie = ST_SetPoint(
          NEW.liniengeometrie,
          0,
          ukos_base.snap_to_topo(ST_StartPoint(NEW.liniengeometrie), tolerance)
        );
        NEW.liniengeometrie = ST_SetPoint(
          NEW.liniengeometrie,
          ST_NumPoints(NEW.liniengeometrie) - 1,
          ukos_base.snap_to_topo(ST_EndPoint(NEW.liniengeometrie), tolerance)
        );
      END IF;

      RETURN NEW;
    END;
  $_$;


--
-- Name: FUNCTION snap_to_topology(); Type: COMMENT; Schema: ukos_base; Owner: -
--

COMMENT ON FUNCTION ukos_base.snap_to_topology() IS 'Snappt Verbindungspunkte sowie Start- und Endpunkte von Strassenelementen  auf vorhandene Verbindungspunkte im Toleranzradius. Snappt zusätzlich Start- und Endpunkte von Strassenelementen auf vorhandene Strassenelemente im Toleranzabstand, falls diese nicht vorher schon auf VP gesnappt sind.';


