--
-- Name: liniengeometrie_aendern(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.liniengeometrie_aendern() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$

  DECLARE
    vp_geom  public.geometry(Point, 25833);
    se_beginnt_bei_geom  public.geometry(Point, 25833) = ST_StartPoint(NEW.liniengeometrie);
    se_endet_bei_geom    public.geometry(Point, 25833) = ST_EndPoint(NEW.liniengeometrie);
    aenderungszeit  TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
    sql              TEXT;
    debug           BOOLEAN = false;
  BEGIN

    -- Initialisieren
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
    IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

    -- Prüfe ob sich am Anfang oder Ende etwas geändert hat
    IF ST_Equals(ST_StartPoint(OLD.liniengeometrie), ST_StartPoint(NEW.liniengeometrie)) AND
      ST_Equals(ST_EndPoint(OLD.liniengeometrie), ST_EndPoint(NEW.liniengeometrie)) THEN
      IF debug THEN RAISE NOTICE 'Anfangs- und Endpunkgeometrie sind gleich geblieben.'; END IF;
      -- Anfangs- und Endpunktgeometrie sind gleich geblieben

      -- Änderungszeit anpassen
      NEW.geaendert_am = aenderungszeit;

      -- ToDo:
      -- Prüfen ob bei einer Änderung der Sachdaten und gleicher Geometrie am Anfang und Ende das Strassenelement auch gelöscht und neu angelegt werden soll an Stelle eines Updates.
    ELSE
      IF debug THEN RAISE NOTICE 'Anfangs- oder Endpunkgeometrie hat sind geändert.'; END IF;
      -- Anfangs- oder Endpunktgeometrie hat sich geändert
      IF NOT ST_Equals(ST_StartPoint(OLD.liniengeometrie), se_beginnt_bei_geom) THEN
        IF debug THEN RAISE NOTICE 'Anfangspunkt der Linie hat sich geändert.'; END IF;
        IF NEW.beginnt_bei_vp IS NOT NULL THEN
          if debug THEN RAISE NOTICE 'VP am Anfang des SE wurde übergeben. Frage punktgeometrie vom VP ab.'; END IF;
          sql = FORMAT('SELECT punktgeometrie FROM ukos_okstra.verbindungspunkt WHERE id = %1$L', NEW.beginnt_bei_vp);
          EXECUTE sql INTO vp_geom;
          if debug THEN RAISE NOTICE 'Vergleiche VP Geometrie % mit Anfangspunkt der Linie %', ST_AsText(vp_geom), ST_AsText(se_beginnt_bei_geom); END IF;
          IF ST_Equals(vp_geom, se_beginnt_bei_geom) THEN
            IF debug THEN RAISE NOTICE 'Geometrie vom VP und Anfang der SE stimmen überein.'; END IF;
          ELSE
            if debug THEN RAISE NOTICE 'Geometrie vom VP stimmt nicht mit Anfang des SE überein. Korrigiere punktgeometrie von VP am Anfang mit Änderungsereignis Trigger umgehen.'; END IF;
            sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET geaendert_von_ereignis = %3$L, punktgeometrie = %1$L WHERE id = %2$L', se_beginnt_bei_geom, NEW.beginnt_bei_vp, 'Trigger umgehen');
            EXECUTE sql;
            sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET geaendert_von_ereignis = '''' WHERE id = %1$L', NEW.beginnt_bei_vp);
            EXECUTE sql;
          END IF;
        END IF;
      END IF;
      IF NOT ST_Equals(ST_EndPoint(OLD.liniengeometrie), se_endet_bei_geom) THEN
        IF debug THEN RAISE NOTICE 'Endpunkt der Linie hat sich geändert.'; END IF;
        IF NEW.endet_bei_vp IS NOT NULL THEN
          if debug THEN RAISE NOTICE 'VP am Ende des SE wurde übergeben. Frage punktgeometrie vom VP ab.'; END IF;
          sql = FORMAT('SELECT punktgeometrie FROM ukos_okstra.verbindungspunkt WHERE id = %1$L', NEW.endet_bei_vp);
          EXECUTE sql INTO vp_geom;
          if debug THEN RAISE NOTICE 'Vergleiche VP Geometrie % mit Endpunkt der Linie %', ST_AsText(vp_geom), ST_AsText(se_endet_bei_geom); END IF;
          IF ST_Equals(vp_geom, se_endet_bei_geom) THEN
            IF debug THEN RAISE NOTICE 'Geometrie vom VP und Ende der SE stimmen überein.'; END IF;
          ELSE
            if debug THEN RAISE NOTICE 'Geometrie vom VP stimmt nicht mit Ende des SE überein. Korrigiere punktgeometrie von VP am Ende mit Änderungsereignis Trigger umgehen.'; END IF;
            sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET geaendert_von_ereignis = %3$L, punktgeometrie = %1$L WHERE id = %2$L', se_endet_bei_geom, NEW.endet_bei_vp, 'Trigger umgehen');
            EXECUTE sql;
            sql = FORMAT('UPDATE ukos_okstra.verbindungspunkt SET geaendert_von_ereignis = '''' WHERE id = %1$L', NEW.endet_bei_vp);
            EXECUTE sql;
          END IF;
        END IF;
      END IF;

      IF NEW.beginnt_bei_vp IS NULL OR NEW.endet_bei_vp IS NULL THEN
        if debug THEN RAISE NOTICE 'VP des Anfangs- oder Endpunktes wurde nicht übergeben. Lösche deshalb das Strassenelement.'; END IF;
        -- Lösche Strassenelement
        EXECUTE '
          DELETE FROM ukos_okstra.strassenelement
          WHERE
            gueltig_bis > $1 AND
            id = $2
        '
        USING aenderungszeit, NEW.id;

        -- Lege ein neues Strassenelement an
        if debug THEN RAISE NOTICE 'Lege neues Strassenelement an.'; END IF;
        NEW.id = uuid_generate_v4();
        NEW.geaendert_am = aenderungszeit;
        EXECUTE 'INSERT INTO ukos_okstra.strassenelement SELECT $1.*'
        USING NEW;
        RAISE NOTICE 'Neues Strassenelement mit id: % angelegt', NEW.id;
        IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NULL.', TG_NAME, TG_TABLE_NAME; END IF;
        RETURN NULL;
        -- UPDATE SE und folgende Trigger werden nicht ausgeführt
      END IF;
    END IF;

    IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
    RETURN NEW;
  END;
$_$;


