-- Name: update_topogeom(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.update_topogeom() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
  DECLARE
    tolerance NUMERIC;
    debug     BOOLEAN = false;
  BEGIN
    --------------------------------------------------------------------------------------------------------
    -- Initialisieren
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
    EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;

    IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

    IF (
      ST_Equals(ST_StartPoint(OLD.liniengeometrie), ST_StartPoint(NEW.liniengeometrie)) AND
      ST_Equals(ST_EndPoint(OLD.liniengeometrie), ST_EndPoint(NEW.liniengeometrie))
    )
    THEN
      IF debug THEN RAISE NOTICE 'Ändere TopoGeom.'; END IF;
      EXECUTE FORMAT('
        SELECT
          topology.ST_ChangeEdgeGeom(
            %1$L,
            (
              SELECT
                e[1]
              FROM
                (
                  SELECT
                    topology.GetTopoGeomElements(liniengeometrie_topo) e
                  FROM
                    ukos_okstra.strassenelement
                  WHERE
                    id = %2$L
                  LIMIT 1
                ) e_tab
            ),
            %3$L
          )
      ', 'ukos_topo', NEW.id, NEW.liniengeometrie, 'ukos_topo');
    ELSE
      IF debug THEN RAISE NOTICE 'Lösche TopoGeom und legen sie neu an.'; END IF;
      EXECUTE FORMAT('SELECT ukos_base.delete_topogeom_func(%1$L)', NEW.id);

      -- Remove isolated Nodes
      EXECUTE FORMAT('
        SELECT topology.ST_RemoveIsoNode(%1$L, node_id)
        FROM ukos_topo.node
        WHERE containing_face = 0
      ', 'ukos_topo');

      -- Add new se to topology
      EXECUTE FORMAT('
        UPDATE
          ukos_okstra.strassenelement
        SET
          liniengeometrie_topo = topology.toTopoGeom(liniengeometrie, %2$L, 1, %3$s)
        WHERE
          id = %1$L
      ', NEW.id, 'ukos_topo', tolerance);
    END IF;

    IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
    RETURN NEW;
  END;
$_$;


