COMMENT ON FUNCTION ukos_okstra.update_with_sep() IS 'Aktualisiert den Strassenausstattungspunkt, ändert ggf. den SEP an dem er hängt oder erzeugt einen neuen SEP oder übernimmt die ID des SEP, der an der neuen Position gefunden wurde.';


--
-- Name: validate_querschnittstreifen(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.validate_querschnittstreifen() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
		DECLARE
			sql text;
			sum RECORD;
			aenderungszeit TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
		BEGIN

			RAISE NOTICE 'Validate Querschnittstreifen.';
			IF NEW.in_verkehrsflaeche IS NULL THEN
				RAISE EXCEPTION 'in_verkehrsflaeche ist leer! Es muss angegeben werden in welcher Verkehrsfläche der Querschnittstreifen liegt.';
			END IF;

			IF NEW.flaechengeometrie IS NULL THEN
				IF NEW.id_strassenelement IS NULL THEN
					RAISE EXCEPTION 'id_strassenelement ist leer. Wenn keine Geometrie übergeben wird, muss eine Strassenelement ID übergeben werden!';
				END IF;
			ELSE
				-- Extrahiere die erste Geometrie des Multipolygons
				IF ST_GeometryType(NEW.flaechengeometrie) = 'MultiPolygon' THEN
					NEW.flaechengeometrie = ST_GeometryN(NEW.flaechengeometrie, 1);
				END IF;

				-- Weise Anfrage zurück, wenn flaechengeometrie mehr als 2 Schnittpunkte mit Strassenelementen hat
				-- und wenn mehr als ein Strassenelment geschnitten werden
				sql = '
					SELECT
						count(id) se,
						sum(ST_NumGeometries(ST_Intersection(liniengeometrie, ST_ExteriorRing(''' || NEW.flaechengeometrie::text || ''')))) sep
					FROM
						ukos_okstra.strassenelement
					WHERE
						gueltig_bis > ' || quote_literal(aenderungszeit) || ' AND
						ST_Intersects(liniengeometrie, ST_ExteriorRing(''' || NEW.flaechengeometrie::text || '''))
				';
				RAISE NOTICE 'SQL: %', sql;
				EXECUTE sql
				INTO sum;
				IF sum.se > 1 THEN
					RAISE EXCEPTION 'Die Flächengeometrie des Querschnittstreifen schneidet mehr als ein Strassenelement!';
					RETURN NULL;
				END IF;

				IF sum.sep > 2 THEN
					RAISE EXCEPTION 'Die Flächengeometrie des Querschnittstreifen schneidet sich in mehr als 2 Strassenelementpunkten!';
					RETURN NULL;
				END IF;

			END IF;

			RETURN NEW;
		END;
	$$;


--
-- Name: validate_sap(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.validate_sap() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
  DECLARE
    tolerance         NUMERIC;
    sql                TEXT;
    se_id              CHARACTER VARYING;
    sep_id            character varying;
    debug             BOOLEAN = false;
  BEGIN

    -- Initialisieren
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;

    IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus', TG_NAME, TG_TABLE_NAME; END IF;

    IF (NEW.bei_strassenelementpunkt_id IS NULL) THEN
      IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id wurde nicht übergeben.'; END IF;
      IF (NEW.punktgeometrie IS NULL) THEN
        IF debug THEN RAISE NOTICE 'punktgeometrie wurde nicht übergeben.'; END IF;
        IF (NEW.id_strassenelemnt IS NULL) THEN
          RAISE EXCEPTION 'Wenn keine Punktgeometrie gegeben ist, muss mindestens die Strassenelement_id angegeben sein!';
        END IF;
        -- Prüfe ob das Strassenelement existiert
        sql = FORMAT('
          SELECT id
          FROM ukos_okstra.strassenelement
          WHERE
            id = %1$L AND
            gueltig_bis > %2$L
        ', NEW.id_strassenelement, aenderungszeit);
        EXECUTE sql INTO se_id;
        if se_id IS NULL THEN
          RAISE EXCEPTION 'Das Strassenelement existiert nicht. Kann Strassenelementpunkt nicht zuordnen!';
        END IF;

        -- Prüfe ob Station und Abszisse angegeben sind.
        IF NEW.station IS NULL OR NEW.abstand_zur_bestandsachse IS NULL THEN
          RAISE EXception 'Es muss die Station und der Abstand zur Bestandsachse angegeben werden.';
        END IF;

        -- Berechne Punktkoordinate aus Station, Abszisse auf SE.
        -- ToDo berechne auch über und unter dem Ende des SE.
        sql = FORMAT('
          SELECT gdi_lineinterpolatepointwithoffset(liniengeometrie, %1$s, %2$s)
          FROM ukos_okstra.strassenelement
          WHERE id = %3$L
        ', NEW.station, NEW.abstand_zur_bestandsachse, id_strassenelemnt);
        EXECUTE sql INTO NEW.punktgeometrie;
      END IF;
    ELSE
      IF debug THEN RAISE NOTICE 'bei_strassenelementpunkt_id wurde übergeben.'; END IF;
      sql = FORMAT('
        SELECT
          id
        FROM
          ukos_okstra.strassenelementpunkt
        WHERE
          id = %1$L
      ', NEW.bei_strassenelementpunkt_id);
      EXECUTE sql
      INTO sep_id;
      IF (sep_id IS NULL) THEN
        RAISE EXCEPTION 'Der Strassenelementpunkt mit der ID: % existiert nicht!', NEW.bei_strassenelementpunkt_id;
      END IF;
    END IF;
    -- Im Ergebnis hat der SAP eine bei_strassenelementpunkt_id und/oder eine punktgeometrie
    IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;
    RETURN NEW;
  END;
$_$;


--
-- Name: validate_strassenelement(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.validate_strassenelement() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
  DECLARE
    vp_beginnt_bei_geometrie  public.geometry(Point, 25833) = ST_StartPoint(NEW.liniengeometrie);
    vp_endet_bei_geometrie    public.geometry(Point, 25833) = ST_EndPoint(NEW.liniengeometrie);
    rec                        RECORD;
    tolerance                  NUMERIC;
    current_query text = current_query();
    debug                      BOOLEAN;
  BEGIN
    --------------------------------------------------------------------------------------------------------
    -- Initialisierung
    EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;
    EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
    IF debug THEN RAISE NOTICE 'Führe Trigger % on % aus.', TG_NAME, TG_TABLE_NAME; END IF;

    --------------------------------------------------------------------------------------------------------
    -- Exceptions

    -- Prüfe ob eine Liniengeometrie existiert
    IF NEW.liniengeometrie IS NULL THEN
      RAISE EXCEPTION 'Liniengeometrie leer. Strassenelement muss eine Liniengeometrie haben.';
    END IF;

    -- Prüfe ob eine stelle_id existiert
    IF TG_OP = 'INSERT' AND NEW.stelle_id IS NULL THEN
      RAISE EXCEPTION 'Stelle_id ist leer. Strassenelement muss eine stelle_id haben.';
    END IF;

    -- Prüfe ob Strassenelement mit gleicher Geometrie schon existiert
    FOR rec IN EXECUTE 'SELECT id FROM ukos_okstra.strassenelement WHERE '
      || 'gueltig_bis > now() AND '
      || 'ST_Equals($1, liniengeometrie)'
      USING NEW.liniengeometrie
    LOOP
      RAISE EXCEPTION 'Strassenelement mit gleicher Liniengeometrie existiert schon.';
    END LOOP;
    -- ToDo: Prüfen ob neues Strassenelement
    --        - vorhandenes Strassenelemnt schneidet
    --        - Verbindungspunkt schneidet
    --        - auf einem anderen Strassenelement liegt
    --------------------------------------------------------------------------------------------------------
    -- Berechnungen
    -- Berechnung der Länge
    IF debug THEN RAISE NOTICE 'Trigger % on Tabelle %: Berechne neue Länge für Strassenelement.', TG_NAME, TG_TABLE_NAME; END IF;
    NEW.laenge = ST_Length(NEW.liniengeometrie);
    PERFORM ukos_base.log(current_query, TG_NAME, TG_WHEN, TG_OP, format('%s.%s', TG_TABLE_SCHEMA, TG_TABLE_NAME), format('Berechne neue Länge für SE: %s', NEW.id));
    IF debug THEN RAISE NOTICE 'Beende Trigger % on % mit RETURN NEW.', TG_NAME, TG_TABLE_NAME; END IF;

    RETURN NEW;
  END;
$_$;


--
-- Name: validate_strassenelementpunkt(); Type: FUNCTION; Schema: ukos_okstra; Owner: -
--

CREATE FUNCTION ukos_okstra.validate_strassenelementpunkt() RETURNS trigger
    LANGUAGE plpgsql
    AS $_$
		DECLARE
			rec 												RECORD;
			sql 												text;
			tolerance 									NUMERIC;
			accuracy 										NUMERIC;
			decimals 										INTEGER;
			anzahl_strassenelemente 		INTEGER;
			strassenelementsuchabstand integer;
			aenderungszeit 							TIMESTAMP WITH time zone = timezone('utc-1'::text, now());
			debug 											BOOLEAN;
		BEGIN
			--------------------------------------------------------------------------------------------------------
			-- Initialisierung
			EXECUTE 'SELECT value FROM ukos_base.config WHERE key = $1' USING 'Debugmodus' INTO debug;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Topologietolerance' INTO tolerance;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Koordinatengenauigkeit' INTO accuracy;
			EXECUTE 'SELECT value::NUMERIC FROM ukos_base.config WHERE key = $1' USING 'Strassenelementsuchabstand' INTO strassenelementsuchabstand;
			decimals = CASE WHEN log(1 / accuracy) < 0 THEN 0 ELSE round(log(1 / accuracy))::INTEGER END;
			--------------------------------------------------------------------------------------------------------
			-- Exceptions

			-- Prüfe ob eine Zuordnung zum Strassenelement existiert
			IF NEW.auf_strassenelement IS NULL THEN
				IF NEW.punktgeometrie IS NULL THEN
					RAISE EXCEPTION 'Da der Strassenelementpunkt keine Geometrie hat, muss mindestens ein Strassenelement angegeben sein!';
				ELSE
					EXECUTE '
						SELECT
							id
						FROM
							ukos_okstra.strassenelement
						WHERE
							gueltig_bis > $1 AND
							ST_DWithin(liniengeometrie, $2, $3)
						ORDER BY
							ST_Distance(liniengeometrie, $2)
						LIMIT 1
					'
					USING aenderungszeit, NEW.punktgeometrie, strassenelementsuchabstand
					INTO NEW.auf_strassenelement;
				END IF;
			END IF;

			-- Prüfe ob das angegebene Strassenelement existiert und gueltig ist
			EXECUTE '
				SELECT count(id)
				FROM ukos_okstra.strassenelement
				WHERE
					gueltig_bis > $1 AND
					id = $2
			'
			USING aenderungszeit, NEW.auf_strassenelement
			INTO anzahl_strassenelemente;

			IF anzahl_strassenelemente = 0 THEN
				RAISE EXCEPTION 'Das Strassenelement existiert nicht!';
			END IF;

			-- Prüfe ob Punktgeometrie und/oder Station im Strassenelementpunkt angegeben sind
			-- und berechne jeweils fehlendes Element und fange Linie wenn Punkt in tolerance zu Linie liegt
			IF NEW.punktgeometrie IS NULL THEN
				IF NEW.station IS NULL THEN
					RAISE EXCEPTION 'Punktgeometrie und Station sind leer. Ein Strassenelementpunkt muss entweder eine Punktgeometrie oder eine Stationierungsangabe haben!';
				ELSE
					IF debug THEN RAISE NOTICE 'Berechne Punktgeometrie entlang des Strassenelementes: % aus Station: % und Abstand zur Bestandsachse: %', NEW.auf_strassenelement, NEW.station, NEW.abstand_zur_bestandsachse; END IF;
					IF abs(NEW.abstand_zur_bestandsachse) > 0 AND abs(NEW.abstand_zur_bestandsachse) <= tolerance THEN
						IF debug THEN RAISE NOTICE 'Reduziere den Abstand zur Linie: % auf 0', NEW.auf_strassenelement; END IF;
						NEW.abstand_zur_bestandsachse = 0;
					END IF;
					NEW.station = round(NEW.station, decimals);
					IF debug THEN RAISE NOTICE 'Runde Station auf: %', NEW.station; END IF;
					EXECUTE '
						SELECT
							gdi_LineInterpolatePointWithOffset(liniengeometrie, $1, $2)
						FROM
							ukos_okstra.strassenelement
						WHERE
							id = $3
					'
					USING NEW.station, NEW.abstand_zur_bestandsachse, NEW.auf_strassenelement
					INTO NEW.punktgeometrie;
					if debug THEN RAISE NOTICE 'Korrigiere Punktgeometrie auf: %', ST_AsText(NEW.punktgeometrie); END IF;
				END IF;
			ELSE
				IF debug THEN RAISE NOTICE 'Punktgeometrie in Strassenelementpunkt vorhanden.'; END IF;
				IF NEW.station IS NULL OR NEW.abstand_zur_bestandsachse IS NULL THEN
					IF debug THEN RAISE NOTICE 'Berechne Station und Abstand zur Bestandsachse aus Punktgeometrie: %', ST_AsText(NEW.punktgeometrie); END IF;
					EXECUTE '
						SELECT
							foot_point, ordinate, abscissa
						FROM
							gdi_LineLocatePointWithOffset(
								(
									SELECT
										liniengeometrie
									FROM
										ukos_okstra.strassenelement
									WHERE
										id = $2
								),
								$1
							) AS (
								foot_point GEOMETRY,
								ordinate NUMERIC,
								abscissa NUMERIC
							)
					'
					USING NEW.punktgeometrie, NEW.auf_strassenelement
					INTO rec;

					NEW.station = round(rec.ordinate, decimals);
					IF debug THEN RAISE NOTICE 'Station auf: % gesetzt.', NEW.station; END IF;
					IF abs(rec.abscissa) <= tolerance THEN
						NEW.abstand_zur_bestandsachse = 0;
						NEW.punktgeometrie = rec.foot_point;
						IF debug THEN RAISE NOTICE 'Abstand zur Bestandsachse auf 0 gesetzt und Punktgeometrie auf: %', ST_AsText(NEW.punktgeometrie); END IF;
					ELSE
						NEW.abstand_zur_bestandsachse = rec.abscissa;
						IF debug THEN RAISE NOTICE 'Abstand zur Bestandsachse mit Toleranz: % auf: % gesetzt.', tolerance, NEW.abstand_zur_bestandsachse; END IF;
					END IF;
				END IF;
			END IF;

			-- ToDo:
			-- Was machen mit Strassenelementpunkten, die negative Station oder Station länger als Strassenelement haben? Zulassen?

			NEW.punktgeometrie = ST_SnapToGrid(NEW.punktgeometrie, accuracy);
			NEW.abstand_zur_bestandsachse = round(NEW.abstand_zur_bestandsachse, decimals);
			IF debug THEN RAISE NOTICE 'Abstand gerundet auf: %.', NEW.abstand_zur_bestandsachse; END IF;
			IF (abs(NEW.abstand_zur_bestandsachse) > 0) THEN
				-- Berechne Fußpunkt der Punktgeometrie
				sql = FORMAT('
					SELECT
						ST_LineInterpolatePoint(liniengeometrie, ST_LineLocatePoint(liniengeometrie, %1$L))
					FROM
						(
							SELECT
								gdi_extendline(liniengeometrie, 10, %3$s, 10, %3$s) AS liniengeometrie
							FROM
								ukos_okstra.strassenelement
							WHERE
								id = %2$L
						) se
				', NEW.punktgeometrie, NEW.auf_strassenelement, strassenelementsuchabstand);
				EXECUTE sql
				INTO NEW.fusspunkt;
			ELSE
				NEW.fusspunkt = NULL;
			END IF;

		RETURN NEW;
	END;
	$_$;


