    gemeindeverband.schluessel AS schluessel_gemeindeverband,
    ((kreis.schluessel)::text || (gemeinde.schluessel)::text) AS schluessel_gemeinde_alkis,
    gemeindeteil.bezeichnung AS bezeichnung_gemeindeteil,
