   FROM ukos_kataster.gemeinde,
    ukos_kataster.gemeindeteil,
    ukos_kataster.gemeindeverband,
    ukos_kataster.kreis
  WHERE (((gemeinde.id_gemeindeverband)::text = (gemeindeverband.id)::text) AND ((gemeindeteil.id_gemeinde)::text = (gemeinde.id)::text) AND ((gemeindeverband.id_kreis)::text = (kreis.id)::text) AND (gemeindeteil.gueltig_von <= timezone('utc-1'::text, now())) AND (gemeindeteil.gueltig_bis >= timezone('utc-1'::text, now())) AND (kreis.gueltig_von <= timezone('utc-1'::text, now())) AND (kreis.gueltig_bis >= timezone('utc-1'::text, now())) AND (gemeindeverband.gueltig_von <= timezone('utc-1'::text, now())) AND (gemeindeverband.gueltig_bis >= timezone('utc-1'::text, now())) AND (gemeinde.gueltig_von <= timezone('utc-1'::text, now())) AND (gemeinde.gueltig_bis >= timezone('utc-1'::text, now())));


