SELECT
  g.id INTO @group_id
FROM
  u_groups g
  LEFT JOIN u_groups og ON g.obergruppe = og.id
WHERE
  (
    og.Gruppenname = 'B-Pläne' OR
    og.Gruppenname IS NULL
  )
  AND g.Gruppenname = 'Bebauung'
ORDER BY og.Gruppenname DESC
LIMIT 1;

SELECT
  id INTO @connection_id
FROM
  connections
WHERE
  host = 'pgsql'
  AND port = 5432
  AND dbname = 'kvwmapsp'
  AND user = 'kvwmap'
LIMIT 1;

